C
      SUBROUTINE INIT4 (WI, RHI, TEMPI)
      INCLUDE 'isrpia.inc'
      DIMENSION WI(NCOMP)
C      REAL      IC,GII,GI0,XX,LN10
      DOUBLE PRECISION      LN10
      PARAMETER (LN10=2.3025851D0)
      INTEGER   I, IRH
C
C *** SAVE INPUT VARIABLES IN COMMON BLOCK ******************************
C
      IF (IPROB.EQ.0) THEN                 ! FORWARD CALCULATION
         DO 10 I=1,NCOMP
            W(I) = MAX(WI(I), TINY)
10       CONTINUE
      ELSE
         DO 15 I=1,NCOMP                   ! REVERSE CALCULATION
