C         XK26= XK26 *EXP(-3.0821*(TOT-1.0d0) + 31.8139*COEF)
C         XK27= XK27 *EXP(-5.9908*(TOT-1.0d0) + 38.844*COEF)

      ENDIF
      XK2  = XK21*XK22
