C
C=======================================================================
C
      SUBROUTINE RSTGAMP
      INCLUDE 'isrpia.inc'
      DOUBLE PRECISION GMAX, GTHRESH
      INTEGER I
C
      GTHRESH = 100.D0
      GMAX    = 0.1D0
      DO I=1, NPAIR
         GMAX = MAX(GMAX,GAMA(I))
      END DO
      IF ((GMAX) .GT. (GTHRESH)) THEN
         DO I = 1,NPAIR
            GAMA(I)  = 1.0D-1
            GAMIN(I) = GREAT
            GAMOU(I) = GREAT
         END DO
         CALAOU   = .TRUE.
         FRST     = .TRUE.
      ENDIF
C      
      END SUBROUTINE RSTGAMP
