C *** ISORROPIA CODE II
C *** SUBROUTINE KMFUL4
C *** CALCULATES BINARY ACTIVITY COEFFICIENTS BY KUSIK-MEISSNER METHOD
C     FOR AN AMMONIUM-SULFATD-NITRATD-CHLORIDD-SODIUM-CALCIUM-POTASSIUM-MAGNESIUM
C     AEROSOL SYSTEM.
C
C *** COPYRIGHT 1996-2012, UNIVERSITY OF MIAMI, CARNEGIE MELLON UNIVERSITY,
C *** GEORGIA INSTITUTE OF TECHNOLOGY
C *** WRITTEN BY CHRISTOS FOUNTOUKIS & ATHANASIOS NENES
C *** UPDATE|ADJOINT BY SHANNON CAPPS
C
C=======================================================================
C
      SUBROUTINE KMFUL4 (IONIC,TEMP,G01,G02,G03,G04,G05,G06,G07,G08,G09,
     &                             G10,G11,G12,G15,G16,G17,G18,G19,G20,
     &                             G21,G22,G23)
      DOUBLE PRECISION Ionic, TEMP, SION, CF1, CF2, TC, TI
      DOUBLE PRECISION G01,G02,G03,G04,G05,G06,G07,G08,G09, 
     &        G10,G11,G12,G15,G16,G17,G18,G19,G20,  
     &        G21,G22,G23 
      DOUBLE PRECISION Z01,Z02,Z03,Z04,Z05,Z06,Z07,Z08,Z10,Z11,Z15,Z16,Z17,Z19,Z20,
     &     Z21,Z22,Z23
      DATA Z01,Z02,Z03,Z04,Z05,Z06,Z07,Z08,Z10,Z11,Z15,Z16,Z17,Z19,Z20,
     &     Z21,Z22,Z23/1.0d0, 2.0d0, 1.0d0, 2.0d0, 1.0d0, 1.0d0, 2.0d0, 
     &                 1.0d0, 1.0d0, 1.0d0, 2.0d0, 2.0d0, 2.0d0, 1.0d0, 
     &                 1.0d0, 4.0d0, 2.0d0, 2.0d0/
C
      SION = SQRT(IONIC)
C
C *** Coefficients at 25 oC
C
      CALL MKBI(2.230d0, IONIC, SION, Z01, G01)
      CALL MKBI(-0.19d0, IONIC, SION, Z02, G02)
      CALL MKBI(-0.39d0, IONIC, SION, Z03, G03)
      CALL MKBI(-0.25d0, IONIC, SION, Z04, G04)
      CALL MKBI(-1.15d0, IONIC, SION, Z05, G05)
      CALL MKBI(0.820d0, IONIC, SION, Z06, G06)
      CALL MKBI(-.100d0, IONIC, SION, Z07, G07)
      CALL MKBI(8.000d0, IONIC, SION, Z08, G08)
      CALL MKBI(2.600d0, IONIC, SION, Z10, G10)
      CALL MKBI(6.000d0, IONIC, SION, Z11, G11)
      CALL MKBI(0.930d0, IONIC, SION, Z15, G15)
      CALL MKBI(2.400d0, IONIC, SION, Z16, G16)
      CALL MKBI(-0.25d0, IONIC, SION, Z17, G17)
      CALL MKBI(-2.33d0, IONIC, SION, Z19, G19)
      CALL MKBI(0.920d0, IONIC, SION, Z20, G20)
      CALL MKBI(0.150d0, IONIC, SION, Z21, G21)
      CALL MKBI(2.320d0, IONIC, SION, Z22, G22)
      CALL MKBI(2.900d0, IONIC, SION, Z23, G23)
C
C *** Correct for T other than 298 K
C
      TI  = TEMP-273.0
      TC  = TI-25.0
      IF (ABS(TC) .GT. 1.0) THEN
         CF1 = 1.125-0.005*TI
         CF2 = (0.125-0.005*TI)*(0.039*IONIC**0.92-0.41*SION/(1.+SION))
         G01 = CF1*G01 - CF2*Z01
         G02 = CF1*G02 - CF2*Z02
         G03 = CF1*G03 - CF2*Z03
         G04 = CF1*G04 - CF2*Z04
         G05 = CF1*G05 - CF2*Z05
         G06 = CF1*G06 - CF2*Z06
         G07 = CF1*G07 - CF2*Z07
         G08 = CF1*G08 - CF2*Z08
         G10 = CF1*G10 - CF2*Z10
         G11 = CF1*G11 - CF2*Z11
         G15 = CF1*G15 - CF2*Z15
         G16 = CF1*G16 - CF2*Z16
         G17 = CF1*G17 - CF2*Z17
         G19 = CF1*G19 - CF2*Z19
         G20 = CF1*G20 - CF2*Z20
         G21 = CF1*G21 - CF2*Z21
         G22 = CF1*G22 - CF2*Z22
         G23 = CF1*G23 - CF2*Z23

      ENDIF
C
      G09 = G06 + G08 - G11
      G12 = G01 + G08 - G11
      G18 = G08 + G20 - G11
C
C *** Return point ; End of subroutine
C
      RETURN
      END
