C
C=======================================================================
C
      SUBROUTINE PUSHERR (IERR,ERRINF)
      INCLUDE 'isrpia.inc'
      CHARACTER ERRINF*(*)
      INTEGER IERR
C
C *** SAVE ERROR CODE IF THERE IS ANY SPACE ***************************
C
      IF (NOFER.LT.NERRMX) THEN
         NOFER         = NOFER + 1
         ERRSTK(NOFER) = IERR
         ERRMSG(NOFER) = ERRINF
         STKOFL        =.FALSE.
      ELSE
         STKOFL        =.TRUE.      ! STACK OVERFLOW
      ENDIF
C
C *** END OF SUBROUTINE PUSHERR ****************************************
C
      END

