C what(1) key, module and SID; SCCS file; date and time of last delta:
C %W% %P% %G% %U%

      SUBROUTINE HRPRODLOSS

C***********************************************************************
C
C  FUNCTION: To compute the production and loss rates for EBI species
C
C  PRECONDITIONS: For the SAPRC07TC family of mechanisms
C
C  RETURN VALUES: None
C
C  KEY SUBROUTINES/FUNCTIONS CALLED: None
C
C  REVISION HISTORY: Created by EBI solver program, July 18, 2014
C
C   18 Jul 14 B.Hutzell: revised to use real(8) variables

C***********************************************************************
      USE HRDATA

      IMPLICIT NONE


C.....INCLUDES: NONE


C.....ARGUMENTS: NONE


C.....PARAMETERS: NONE


C.....EXTERNAL FUNCTIONS: NONE


C.....SAVED VARIABLES: NONE
      CHARACTER( 16 ), SAVE  ::  PNAME = 'HRPRODLOSS'   ! Program name


C.....LOCAL VARIABLES:

      INTEGER ESP

C***********************************************************************


      PROD( NCELL, HNO3             ) =    2.0000D+00 * RXRAT( NCELL,  13 )      ! N2O5=0.2000D+01*HNO3
     &                                +    2.0000D+00 * RXRAT( NCELL,  14 )      ! N2O5=0.2000D+01*HNO3
     &                                +                 RXRAT( NCELL,  25 )      ! OH+NO2=HNO3
     &                                +    2.0000D-01 * RXRAT( NCELL,  39 )      ! NO3+HO2=0.2000D+00*HNO3+...
     &                                +                 RXRAT( NCELL, 211 )      ! HCHO+NO3=HNO3+HO2+CO
     &                                +                 RXRAT( NCELL, 214 )      ! CCHO+NO3=HNO3+MECO3
     &                                +                 RXRAT( NCELL, 217 )      ! RCHO+NO3=HNO3+RCO3
     &                                +                 RXRAT( NCELL, 237 )      ! GLY+NO3=HNO3+0.6300D+...
     &                                +                 RXRAT( NCELL, 240 )      ! MGLY+NO3=HNO3+CO+MECO3
     &                                +                 RXRAT( NCELL, 243 )      ! CRES+NO3=HNO3+BZO
     &                                +                 RXRAT( NCELL, 249 )      ! BALD+NO3=HNO3+BZCO3
     &                                +    5.0000D-01 * RXRAT( NCELL, 260 )      ! MACR+NO3=0.5000D+00*HNO3+...
     &                                +    1.5000D-01 * RXRAT( NCELL, 269 )      ! IPRD+NO3=0.1500D+00*HNO3+...
     &                                +                 RXRAT( NCELL, 277 )      ! HOCCHO+NO3=HNO3+MECO3
     &                                +    9.6700D-01 * RXRAT( NCELL, 280 )      ! ACROLEIN+NO3=0.9670D+...
     &                                +    2.0000D+00 * RXRAT( NCELL, 690 )      ! N2O5=0.2000D+01*HNO3
     &                                +    5.0000D-01 * RXRAT( NCELL, 691 )      ! NO2=0.5000D+00*HNO3+...
      LOSS( NCELL, HNO3             ) =                 RXRAT( NCELL,  27 )      ! HNO3+OH=NO3
     &                                +                 RXRAT( NCELL,  28 )      ! HNO3=OH+NO2

      PROD( NCELL, CO               ) =                 RXRAT( NCELL, 198 )      ! xCO+NO=CO+NO
     &                                +                 RXRAT( NCELL, 200 )      ! xCO+NO3=CO+NO3
     &                                +    5.0000D-01 * RXRAT( NCELL, 201 )      ! xCO+MEO2=0.5000D+00*CO+MEO2
     &                                +    5.0000D-01 * RXRAT( NCELL, 202 )      ! xCO+RO2C=0.5000D+00*CO+RO2C
     &                                +    5.0000D-01 * RXRAT( NCELL, 203 )      ! xCO+RO2XC=0.5000D+00*CO+RO2XC
     &                                +                 RXRAT( NCELL, 204 )      ! xCO+MECO3=CO+MECO3
     &                                +                 RXRAT( NCELL, 205 )      ! xCO+RCO3=CO+RCO3
     &                                +                 RXRAT( NCELL, 206 )      ! xCO+BZCO3=CO+BZCO3
     &                                +                 RXRAT( NCELL, 207 )      ! xCO+MACO3=CO+MACO3
     &                                +                 RXRAT( NCELL, 208 )      ! HCHO=CO+0.2000D+01*HO2
     &                                +                 RXRAT( NCELL, 209 )      ! HCHO=CO
     &                                +                 RXRAT( NCELL, 210 )      ! HCHO+OH=CO+HO2
     &                                +                 RXRAT( NCELL, 211 )      ! HCHO+NO3=CO+HO2+HNO3
     &                                +                 RXRAT( NCELL, 213 )      ! CCHO=CO+HO2+MEO2
     &                                +                 RXRAT( NCELL, 216 )      ! RCHO=CO+xHO2+yROOH+xCCHO+...
     &                                +    3.8000D-01 * RXRAT( NCELL, 219 )      ! ACETONE=0.3800D+00*CO+...
     &                                +    2.0000D+00 * RXRAT( NCELL, 234 )      ! GLY=0.2000D+01*CO+0.2000D+...
     &                                +                 RXRAT( NCELL, 235 )      ! GLY=CO+HCHO
     &                                +    1.2600D+00 * RXRAT( NCELL, 236 )      ! GLY+OH=0.1260D+01*CO+...
     &                                +    1.2600D+00 * RXRAT( NCELL, 237 )      ! GLY+NO3=0.1260D+01*CO+...
     &                                +                 RXRAT( NCELL, 238 )      ! MGLY=CO+HO2+MECO3
     &                                +                 RXRAT( NCELL, 239 )      ! MGLY+OH=CO+MECO3
     &                                +                 RXRAT( NCELL, 240 )      ! MGLY+NO3=CO+HNO3+MECO3
     &                                +    5.2200D-01 * RXRAT( NCELL, 251 )      ! AFG1+O3=0.5220D+00*CO+...
     &                                +    6.9500D-01 * RXRAT( NCELL, 252 )      ! AFG1=0.6950D+00*CO+0.1730D+...
     &                                +    5.2200D-01 * RXRAT( NCELL, 254 )      ! AFG2+O3=0.5220D+00*CO+...
     &                                +    5.8000D-01 * RXRAT( NCELL, 257 )      ! AFG3+O3=0.5800D+00*CO+...
     &                                +    4.5000D-01 * RXRAT( NCELL, 259 )      ! MACR+O3=0.4500D+00*CO+...
     &                                +    6.7000D-01 * RXRAT( NCELL, 262 )      ! MACR=0.6700D+00*CO+0.6700D+...
     &                                +    4.7500D-01 * RXRAT( NCELL, 264 )      ! MVK+O3=0.4750D+00*CO+...
     &                                +    6.0000D-01 * RXRAT( NCELL, 266 )      ! MVK=0.6000D+00*CO+0.4000D+...
     &                                +    4.9800D-01 * RXRAT( NCELL, 268 )      ! IPRD+O3=0.4980D+00*CO+...
     &                                +    1.2330D+00 * RXRAT( NCELL, 270 )      ! IPRD=0.1233D+01*CO+0.4670D+...
     &                                +                 RXRAT( NCELL, 276 )      ! HOCCHO=CO+0.2000D+01*HO2+HCHO
     &                                +    1.0050D+00 * RXRAT( NCELL, 279 )      ! ACROLEIN+O3=0.1005D+01*CO+...
     &                                +    1.1880D+00 * RXRAT( NCELL, 282 )      ! ACROLEIN=0.1188D+01*CO+...
     &                                +    5.1000D-01 * RXRAT( NCELL, 519 )      ! ETHENE+O3=0.5100D+00*CO+...
     &                                +    5.1000D-01 * RXRAT( NCELL, 521 )      ! ETHENE+O3P=0.5100D+00*CO+...
     &                                +    5.2500D-01 * RXRAT( NCELL, 523 )      ! PROPENE+O3=0.5250D+00*CO+...
     &                                +    2.5500D-01 * RXRAT( NCELL, 527 )      ! BUTADIENE13+O3=0.2550D+...
     &                                +    2.7500D-01 * RXRAT( NCELL, 531 )      ! ISOPRENE+O3=0.2750D+00*CO+...
     &                                +    2.9000D-02 * RXRAT( NCELL, 535 )      ! APIN+O3=0.2900D-01*CO+...
     &                                +    3.0000D-01 * RXRAT( NCELL, 538 )      ! ACETYLENE+OH=0.3000D+00*CO+...
     &                                +    1.5000D+00 * RXRAT( NCELL, 539 )      ! ACETYLENE+O3=0.1500D+01*CO+...
     &                                +    3.0300D-01 * RXRAT( NCELL, 553 )      ! OLE1+O3=0.3030D+00*CO+...
     &                                +    2.9900D-01 * RXRAT( NCELL, 557 )      ! OLE2+O3=0.2990D+00*CO+...
     &                                +    2.4900D-01 * RXRAT( NCELL, 563 )      ! TERP+O3=0.2490D+00*CO+...
     &                                +    2.4900D-01 * RXRAT( NCELL, 567 )      ! SESQ+O3=0.2490D+00*CO+...
     &                                +                 RXRAT( NCELL, 592 )      ! HCHO+CL=CO+HO2+HCL
     &                                +    1.2600D+00 * RXRAT( NCELL, 600 )      ! GLY+CL=0.1260D+01*CO+...
     &                                +                 RXRAT( NCELL, 601 )      ! MGLY+CL=CO+HCL+MECO3
     &                                +                 RXRAT( NCELL, 611 )      ! CLCCHO=CO+HO2+RO2C+xCL+...
     &                                +                 RXRAT( NCELL, 651 )      ! ACETYLENE+CL=CO+HO2
      LOSS( NCELL, CO               ) =                 RXRAT( NCELL,  29 )      ! CO+OH=HO2+CO2

      PROD( NCELL, CO2              ) =                 RXRAT( NCELL,  29 )      ! OH+CO=CO2+HO2
     &                                +    4.0000D-01 * RXRAT( NCELL,  69 )      ! PAN=0.4000D+00*CO2+0.6000D+...
     &                                +                 RXRAT( NCELL,  70 )      ! MECO3+NO=CO2+MEO2+NO2
     &                                +                 RXRAT( NCELL,  72 )      ! MECO3+NO3=CO2+MEO2+NO2
     &                                +    9.0000D-01 * RXRAT( NCELL,  73 )      ! MECO3+MEO2=0.9000D+00*CO2+...
     &                                +                 RXRAT( NCELL,  74 )      ! MECO3+RO2C=CO2+MEO2
     &                                +                 RXRAT( NCELL,  75 )      ! MECO3+RO2XC=CO2+MEO2
     &                                +    2.0000D+00 * RXRAT( NCELL,  76 )      ! MECO3+MECO3=0.2000D+01*CO2+...
     &                                +    4.0000D-01 * RXRAT( NCELL,  79 )      ! PAN2=0.4000D+00*CO2+...
     &                                +                 RXRAT( NCELL,  80 )      ! RCO3+NO=CO2+RO2C+xHO2+...
     &                                +                 RXRAT( NCELL,  82 )      ! RCO3+NO3=CO2+RO2C+xHO2+...
     &                                +                 RXRAT( NCELL,  83 )      ! RCO3+MEO2=CO2+HO2+RO2C+...
     &                                +                 RXRAT( NCELL,  84 )      ! RCO3+RO2C=CO2+xHO2+xCCHO+...
     &                                +                 RXRAT( NCELL,  85 )      ! RCO3+RO2XC=CO2+xHO2+xCCHO+...
     &                                +    2.0000D+00 * RXRAT( NCELL,  86 )      ! RCO3+MECO3=0.2000D+01*CO2+...
     &                                +    2.0000D+00 * RXRAT( NCELL,  87 )      ! RCO3+RCO3=0.2000D+01*CO2+...
     &                                +    4.0000D-01 * RXRAT( NCELL,  90 )      ! PBZN=0.4000D+00*CO2+...
     &                                +                 RXRAT( NCELL,  91 )      ! BZCO3+NO=CO2+NO2+BZO+RO2C
     &                                +                 RXRAT( NCELL,  93 )      ! BZCO3+NO3=CO2+NO2+BZO+RO2C
     &                                +                 RXRAT( NCELL,  94 )      ! BZCO3+MEO2=CO2+HO2+RO2C+...
     &                                +                 RXRAT( NCELL,  95 )      ! BZCO3+RO2C=CO2+BZO+RO2C
     &                                +                 RXRAT( NCELL,  96 )      ! BZCO3+RO2XC=CO2+BZO+RO2C
     &                                +    2.0000D+00 * RXRAT( NCELL,  97 )      ! BZCO3+MECO3=0.2000D+01*CO2+...
     &                                +    2.0000D+00 * RXRAT( NCELL,  98 )      ! BZCO3+RCO3=0.2000D+01*CO2+...
     &                                +    2.0000D+00 * RXRAT( NCELL,  99 )      ! BZCO3+BZCO3=0.2000D+01*CO2+...
     &                                +    4.0000D-01 * RXRAT( NCELL, 102 )      ! MAPAN=0.4000D+00*CO2+...
     &                                +                 RXRAT( NCELL, 103 )      ! MACO3+NO=CO2+NO2+HCHO+MECO3
     &                                +                 RXRAT( NCELL, 105 )      ! MACO3+NO3=CO2+NO2+HCHO+MECO3
     &                                +                 RXRAT( NCELL, 106 )      ! MACO3+MEO2=CO2+HO2+0.2000D+...
     &                                +                 RXRAT( NCELL, 107 )      ! MACO3+RO2C=CO2+HCHO+MECO3
     &                                +                 RXRAT( NCELL, 108 )      ! MACO3+RO2XC=CO2+HCHO+MECO3
     &                                +    2.0000D+00 * RXRAT( NCELL, 109 )      ! MACO3+MECO3=0.2000D+01*CO2+...
     &                                +    2.0000D+00 * RXRAT( NCELL, 110 )      ! MACO3+RCO3=0.2000D+01*CO2+...
     &                                +    2.0000D+00 * RXRAT( NCELL, 111 )      ! MACO3+BZCO3=0.2000D+01*CO2+...
     &                                +    2.0000D+00 * RXRAT( NCELL, 112 )      ! MACO3+MACO3=0.2000D+01*CO2+...
     &                                +                 RXRAT( NCELL, 223 )      ! HCOOH+OH=CO2+HO2
     &                                +    5.0900D-01 * RXRAT( NCELL, 224 )      ! CCOOH+OH=0.5090D+00*CO2+...
     &                                +    1.4300D-01 * RXRAT( NCELL, 225 )      ! RCOOH+OH=0.1430D+00*CO2+...
     &                                +    1.7400D-01 * RXRAT( NCELL, 251 )      ! AFG1+O3=0.1740D+00*CO2+...
     &                                +    1.7400D-01 * RXRAT( NCELL, 254 )      ! AFG2+O3=0.1740D+00*CO2+...
     &                                +    1.9000D-01 * RXRAT( NCELL, 257 )      ! AFG3+O3=0.1900D+00*CO2+...
     &                                +    1.1700D-01 * RXRAT( NCELL, 259 )      ! MACR+O3=0.1170D+00*CO2+...
     &                                +    1.2400D-01 * RXRAT( NCELL, 264 )      ! MVK+O3=0.1240D+00*CO2+...
     &                                +    1.4000D-01 * RXRAT( NCELL, 268 )      ! IPRD+O3=0.1400D+00*CO2+...
     &                                +    3.1000D-01 * RXRAT( NCELL, 279 )      ! ACROLEIN+O3=0.3100D+00*CO2+...
     &                                +    1.0200D-01 * RXRAT( NCELL, 282 )      ! ACROLEIN=0.1020D+00*CO2+...
     &                                +    2.0000D-02 * RXRAT( NCELL, 283 )      ! CCOOOH+OH=0.2000D-01*CO2+...
     &                                +                 RXRAT( NCELL, 284 )      ! CCOOOH=CO2+MEO2+OH
     &                                +    1.1000D-01 * RXRAT( NCELL, 285 )      ! RCOOOH+OH=0.1100D+00*CO2+...
     &                                +                 RXRAT( NCELL, 286 )      ! RCOOOH=CO2+xCCHO+yROOH+xHO2+OH
     &                                +    1.2000D-01 * RXRAT( NCELL, 519 )      ! ETHENE+O3=0.1200D+00*CO2+...
     &                                +    2.1500D-01 * RXRAT( NCELL, 523 )      ! PROPENE+O3=0.2150D+00*CO2+...
     &                                +    1.8500D-01 * RXRAT( NCELL, 527 )      ! BUTADIENE13+O3=0.1850D+...
     &                                +    1.2200D-01 * RXRAT( NCELL, 531 )      ! ISOPRENE+O3=0.1220D+00*CO2+...
     &                                +    1.7000D-02 * RXRAT( NCELL, 535 )      ! APIN+O3=0.1700D-01*CO2+...
     &                                +    5.0000D-01 * RXRAT( NCELL, 539 )      ! ACETYLENE+O3=0.5000D+...
     &                                +    8.8000D-02 * RXRAT( NCELL, 553 )      ! OLE1+O3=0.8800D-01*CO2+...
     &                                +    1.6100D-01 * RXRAT( NCELL, 557 )      ! OLE2+O3=0.1610D+00*CO2+...
     &                                +    6.3000D-02 * RXRAT( NCELL, 563 )      ! TERP+O3=0.6300D-01*CO2+...
     &                                +    6.3000D-02 * RXRAT( NCELL, 567 )      ! SESQ+O3=0.6300D-01*CO2+...
      LOSS( NCELL, CO2              ) =   0.0D0

      PROD( NCELL, HO2H             ) =                 RXRAT( NCELL,  37 )      ! HO2+HO2=HO2H
     &                                +                 RXRAT( NCELL,  38 )      ! HO2+HO2=HO2H
      LOSS( NCELL, HO2H             ) =                 RXRAT( NCELL,  41 )      ! HO2H=0.2000D+01*OH
     &                                +                 RXRAT( NCELL,  42 )      ! HO2H+OH=HO2

      PROD( NCELL, SO2              ) =   0.0D0
      LOSS( NCELL, SO2              ) =                 RXRAT( NCELL,  44 )      ! SO2+OH=HO2+SULF+SULRXN

      PROD( NCELL, SULF             ) =                 RXRAT( NCELL,  44 )      ! OH+SO2=SULF+HO2+SULRXN
      LOSS( NCELL, SULF             ) =   0.0D0

      PROD( NCELL, SULRXN           ) =                 RXRAT( NCELL,  44 )      ! OH+SO2=SULRXN+SULF+HO2
      LOSS( NCELL, SULRXN           ) =   0.0D0

      PROD( NCELL, NO2EX            ) =                 RXRAT( NCELL,  46 )      ! NO2=NO2EX
      LOSS( NCELL, NO2EX            ) =                 RXRAT( NCELL,  47 )      ! NO2EX=NO2
     &                                +                 RXRAT( NCELL,  48 )      ! NO2EX=NO2
     &                                +                 RXRAT( NCELL,  49 )      ! NO2EX=OH+HONO

      PROD( NCELL, MEO2             ) =    4.0000D-01 * RXRAT( NCELL,  69 )      ! PAN=0.4000D+00*MEO2+...
     &                                +                 RXRAT( NCELL,  70 )      ! MECO3+NO=MEO2+CO2+NO2
     &                                +                 RXRAT( NCELL,  72 )      ! MECO3+NO3=MEO2+CO2+NO2
     &                                +                 RXRAT( NCELL,  74 )      ! MECO3+RO2C=MEO2+CO2
     &                                +                 RXRAT( NCELL,  75 )      ! MECO3+RO2XC=MEO2+CO2
     &                                +    2.0000D+00 * RXRAT( NCELL,  76 )      ! MECO3+MECO3=0.2000D+...
     &                                +                 RXRAT( NCELL,  86 )      ! RCO3+MECO3=MEO2+0.2000D+...
     &                                +                 RXRAT( NCELL,  97 )      ! BZCO3+MECO3=MEO2+0.2000D+...
     &                                +                 RXRAT( NCELL, 109 )      ! MACO3+MECO3=MEO2+0.2000D+...
     &                                +                 RXRAT( NCELL, 114 )      ! TBUO=MEO2+ACETONE
     &                                +                 RXRAT( NCELL, 148 )      ! xMEO2+NO=MEO2+NO
     &                                +                 RXRAT( NCELL, 150 )      ! xMEO2+NO3=MEO2+NO3
     &                                +    5.0000D-01 * RXRAT( NCELL, 151 )      ! xMEO2+MEO2=0.1500D+01*MEO2
     &                                +    5.0000D-01 * RXRAT( NCELL, 152 )      ! xMEO2+RO2C=0.5000D+00*MEO2+...
     &                                +    5.0000D-01 * RXRAT( NCELL, 153 )      ! xMEO2+RO2XC=0.5000D+...
     &                                +                 RXRAT( NCELL, 154 )      ! xMEO2+MECO3=MEO2+MECO3
     &                                +                 RXRAT( NCELL, 155 )      ! xMEO2+RCO3=MEO2+RCO3
     &                                +                 RXRAT( NCELL, 156 )      ! xMEO2+BZCO3=MEO2+BZCO3
     &                                +                 RXRAT( NCELL, 157 )      ! xMEO2+MACO3=MEO2+MACO3
     &                                +                 RXRAT( NCELL, 213 )      ! CCHO=MEO2+HO2+CO
     &                                +    1.3800D+00 * RXRAT( NCELL, 219 )      ! ACETONE=0.1380D+01*MEO2+...
     &                                +    5.0900D-01 * RXRAT( NCELL, 224 )      ! CCOOH+OH=0.5090D+00*MEO2+...
     &                                +    7.0000D-01 * RXRAT( NCELL, 226 )      ! COOH+OH=0.7000D+00*MEO2+...
     &                                +    1.7300D-01 * RXRAT( NCELL, 252 )      ! AFG1=0.1730D+00*MEO2+...
     &                                +    4.0000D-01 * RXRAT( NCELL, 266 )      ! MVK=0.4000D+00*MEO2+...
     &                                +    2.3400D-01 * RXRAT( NCELL, 282 )      ! ACROLEIN=0.2340D+00*MEO2+...
     &                                +                 RXRAT( NCELL, 284 )      ! CCOOOH=MEO2+CO2+OH
     &                                +                 RXRAT( NCELL, 517 )      ! OH=MEO2
     &                                +    5.1000D-01 * RXRAT( NCELL, 521 )      ! ETHENE+O3P=0.5100D+00*MEO2+...
     &                                +    3.5500D-01 * RXRAT( NCELL, 523 )      ! PROPENE+O3=0.3550D+00*MEO2+...
     &                                +    2.5000D-01 * RXRAT( NCELL, 533 )      ! ISOPRENE+O3P=0.2500D+...
     &                                +    3.0700D-01 * RXRAT( NCELL, 557 )      ! OLE2+O3=0.3070D+00*MEO2+...
     &                                +                 RXRAT( NCELL, 645 )      ! CL=MEO2+HCL
      LOSS( NCELL, MEO2             ) =                 RXRAT( NCELL,  50 )      ! MEO2+NO=NO2+HCHO+HO2
     &                                +                 RXRAT( NCELL,  51 )      ! MEO2+HO2=COOH
     &                                +                 RXRAT( NCELL,  52 )      ! MEO2+HO2=HCHO
     &                                +                 RXRAT( NCELL,  53 )      ! MEO2+NO3=HCHO+HO2+NO2
